def top_two_indices(lst):
    if not lst:
        return []

    if len(lst) == 1:
        return [0]

    top1, top2 = (0, 1) if lst[0] > lst[1] else (1, 0)

    for i, num in enumerate(lst[2:], 2):
        if num > lst[top1]:
            top2 = top1
            top1 = i
        elif num > lst[top2]:
            top2 = i

    return [top1, top2]
